"use strict";var time=new Date;function countDown(){var e=time.getTime()-Date.now();return{hours:parseInt(e/1e3/3600),min:parseInt(e/1e3/60%60),sec:parseInt(e/1e3%60)}}time.setHours(time.getHours()+3),setInterval(function(){renderCountDown()},800);var hour_ele=document.getElementById("hour"),min_ele=document.getElementById("min"),sec_ele=document.getElementById("sec");function renderCountDown(){var e=countDown();hour_ele.innerHTML=addZ(e.hours),min_ele.innerHTML=addZ(e.min),sec_ele.innerHTML=addZ(e.sec)}function addZ(e){return 10<=e?e:"0"+e}renderCountDown();